import React from 'react';
import ScrollToBottom from 'react-scroll-to-bottom';

import Message from '../Message/Message';
import './Messages.css';

const Messages = ({ messages, name, setMessage }) => (
    <ScrollToBottom className="messages">
        {messages.map((message, i) => <div key={i}><Message setMessage={setMessage} message={message} name={name} /></div>)}
    </ScrollToBottom>

)

export default Messages;
