import React, { useState } from 'react';
import ReactEmoji from "react-emoji";
import Reactions from '../reactions/reactions';

import './Message.css';

const Message = ({ message: { user, text, time, reaction }, name, setMessage }) => {
    const [hover, setHover] = useState(false);

    let isSentByCurrentUser = false;
    const trimName = name.trim().toLowerCase();
    if (user === trimName) {
        isSentByCurrentUser = true
    }
    console.log(text, 'TEXT');
    console.log(reaction);
    return (
        isSentByCurrentUser ? (
            <div className="message__Container fadeIn justify__end" >
                <p className="sent__Text pr-10">{trimName}</p>
                <div className="message__BoxContainer justify__end" onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)}>
                    {hover ? <Reactions reaction={reaction} setMessage={setMessage} /> : null}
                    <div className="message__Box background__Blue" >
                        {text.includes("data:image") ? <img width="400px" src={text} />
                            : text.includes("giphy.com/media") ? <img width="300px" src={text} />
                                : <p className="message__Text color__white">{ReactEmoji.emojify(text)}</p>}
                    </div>
                    <p className="sent__Time pr-10 justify__end">{time}</p>
                </div>
            </div>
        ) : (
                <div className="message__Container fadeIn justify__start" >
                    <div className="message__BoxContainer justify__start" onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)}>
                        {hover ? <Reactions reaction={reaction} setMessage={setMessage} className="justify__start" /> : null}
                        <div className="message__Box" >
                            {text.includes("data:image") ? <img width="400px" src={text} />
                                : text.includes("giphy.com/media") ? <img width="300px" src={text} />
                                    : <p className="message__Text color__Dark">{ReactEmoji.emojify(text)}</p>}
                        </div>
                        <p className="sent__Time pr-10 justify__start">{time}</p>
                    </div>
                    <p className="sent__Text pl-10">{user}</p>
                </div>
            )
    )
}

export default Message;
